sprites.onOverlap(SpriteKind.Player, SpriteKind.Enemy, function (sprite, otherSprite) {
    if (Mario.vy > 0 && Mario.y < otherSprite.y) {
        sprites.destroy(Goomba)
        music.play(music.melodyPlayable(music.thump), music.PlaybackMode.UntilDone)
    } else {
        if (Big == true) {
            if (Mario.overlapsWith(Goomba)) {
                Mario.scale = 1
            }
        }
        if (Big == false) {
            if (Mario.overlapsWith(Goomba)) {
                game.gameOver(false)
                game.setGameOverEffect(false, effects.splatter)
            }
        }
    }
})
